import React, { useState, useEffect } from 'react';
import { startOfMonth, endOfMonth, startOfWeek, endOfWeek, eachDayOfInterval, format, addMonths, subMonths, isSameDay } from 'date-fns';
import EventForm from './components/EventForm';
import CalendarDay from './components/CalendarDay';
import { DragDropContext } from 'react-beautiful-dnd';


const App = () => {
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const [events, setEvents] = useState(() => JSON.parse(localStorage.getItem('events')) || []);
  const [selectedDate, setSelectedDate] = useState(null);
  const [editingEvent, setEditingEvent] = useState(null);

  useEffect(() => {
    localStorage.setItem('events', JSON.stringify(events));
  }, [events]);

  const days = eachDayOfInterval({
    start: startOfWeek(startOfMonth(currentMonth)),
    end: endOfWeek(endOfMonth(currentMonth))
  });

  const addEvent = (event) => {
    setEvents([...events, { ...event, id: Date.now() }]);
    setSelectedDate(null);
  };

  const updateEvent = (updated) => {
    setEvents(events.map(e => e.id === updated.id ? updated : e));
    setEditingEvent(null);
  };

  const deleteEvent = (id) => {
    setEvents(events.filter(e => e.id !== id));
    setEditingEvent(null);
  };

  <DragDropContext onDragEnd={handleDragEnd}>
  <div className="calendar">
    {days.map((day, index) => (
      <CalendarDay
        key={index}
        day={day}
        events={events.filter(e => isSameDay(new Date(e.date), day))}
        onAdd={() => setSelectedDate(day)}
        onEdit={setEditingEvent}
        dayIndex={index}
      />
    ))}
  </div>
</DragDropContext>


  return (
    <div>
      <h1 style={{ textAlign: 'center' }}>Event Calendar</h1>
      <div style={{ textAlign: 'center' }}>
        <button onClick={() => setCurrentMonth(subMonths(currentMonth, 1))}>Previous</button>
        <strong style={{ margin: '0 1rem' }}>{format(currentMonth, 'MMMM yyyy')}</strong>
        <button onClick={() => setCurrentMonth(addMonths(currentMonth, 1))}>Next</button>
      </div>

      <div className="calendar">
        {days.map((day, index) => (
          <CalendarDay
            key={index}
            day={day}
            events={events.filter(e => isSameDay(new Date(e.date), day))}
            onAdd={() => setSelectedDate(day)}
            onEdit={setEditingEvent}
          />
        ))}
      </div>

      {selectedDate && <EventForm date={selectedDate} onSave={addEvent} onCancel={() => setSelectedDate(null)} />}
      {editingEvent && <EventForm event={editingEvent} onSave={updateEvent} onDelete={deleteEvent} onCancel={() => setEditingEvent(null)} />}
    </div>
  );
};

export default App;
